mapper.select:
SELECT

FROM tp_car_track a

WHERE a.id = #{id}

        SELECT

        FROM tp_car_track a


            a.del_flag = #{DEL_FLAG_NORMAL}
            ${dataScope}

                AND a.name_begin LIKE
                '%'||#{nameBegin}||'%'
                '%'+#{nameBegin}+'%'
                concat('%',#{nameBegin},'%')


                AND a.name_end LIKE
                '%'||#{nameEnd}||'%'
                '%'+#{nameEnd}+'%'
                concat('%',#{nameEnd},'%')


                AND a.time_begin BETWEEN #{beginTimeBegin} AND #{endTimeBegin}


                AND a.time_end BETWEEN #{beginTimeEnd} AND #{endTimeEnd}


				AND a.time_begin >= #{timeBegin}


				AND a.time_end <= #{timeEnd}


                AND a.km BETWEEN #{beginKm} AND #{endKm}


                AND a.driver_type = #{driverType}


                AND a.maintenance_id = #{maintenance.id}


                AND a.job_desc LIKE
                '%'||#{jobDesc}||'%'
                '%'+#{jobDesc}+'%'
                concat('%',#{jobDesc},'%')


                AND a.user_id = #{user.id}


                AND a.what_day = #{whatDay}


        		AND car.name like  concat('%',#{car.name},'%')




                ORDER BY ${page.orderBy}


                ORDER BY a.time_begin DESC

SELECT

FROM tp_car_track a


    a.del_flag = #{DEL_FLAG_NORMAL}
    ${dataScope}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE tp_car_track SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
