mapper.select:
SELECT 

FROM tp_maintenance a

WHERE a.id = #{id}

SELECT 

FROM tp_maintenance a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.num = #{num}


		AND concat(a.job_type,',') like concat('%',#{jobType},',%')


		AND a.job_description = #{jobDescription}


		AND a.job_source = #{source}


		AND a.area_id = #{area.id}


		AND a.roadcross_id = #{roadcross.id}


		AND a.road_id = #{road.id}


		AND a.address LIKE 
			'%'||#{address}||'%'
			'%'+#{address}+'%'
			concat('%',#{address},'%')


		AND a.nearest_poi LIKE 
			'%'||#{nearestPoi}||'%'
			'%'+#{nearestPoi}+'%'
			concat('%',#{nearestPoi},'%')


		AND a.send_by = #{sendBy.id}


		AND  DATE_FORMAT(a.send_date,'%Y-%m-%d') >= DATE_FORMAT(#{beginSendDate},'%Y-%m-%d')


		AND  DATE_FORMAT(a.send_date,'%Y-%m-%d') <= DATE_FORMAT(#{endSendDate},'%Y-%m-%d')


		AND a.office = #{office.id}


		AND a.leader_by = #{leaderBy.id}


		AND  DATE_FORMAT(a.job_begin_date,'%Y-%m-%d') >= DATE_FORMAT(#{beginJobBeginDate},'%Y-%m-%d')


		AND  DATE_FORMAT(a.job_begin_date,'%Y-%m-%d') <= DATE_FORMAT(#{endJobBeginDate},'%Y-%m-%d')


		AND a.status = #{status}


		AND a.malfunction_type = #{malfunctionType}


		AND a.what_day = #{whatDay}




		ORDER BY ${page.orderBy}


		ORDER BY a.send_date DESC

SELECT 

FROM tp_maintenance a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tp_maintenance SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT tmi.remarks as verb, tmi.category, tmp.`name` as itemName, concat( tmi.remarks,' ', tmi.category,'-',tmp.`name` ) AS projectName, tmp.unit, SUM( tmi.count ) AS allCount, tmi.price, SUM( tmi.money ) AS allMoney FROM tp_maintenance a LEFT JOIN sys_area area ON area.id = a.area_id LEFT JOIN tp_road_crossing roadcross ON roadcross.id = a.roadcross_id LEFT JOIN tp_road road ON road.id = a.road_id LEFT JOIN sys_user sendBy ON sendBy.id = a.send_by LEFT JOIN sys_office office ON office.id = a.office LEFT JOIN sys_user leaderBy ON leaderBy.id = a.leader_by LEFT JOIN tp_maintenance_item tmi ON a.id = tmi.maintenance_id LEFT JOIN tp_material_part tmp on tmp.id = tmi.material_part_id
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.num = #{num}
AND concat(a.job_type,',') like concat('%',#{jobType},',%')
AND a.job_description = #{jobDescription}
AND a.job_source = #{source}
AND a.area_id = #{area.id}
AND a.roadcross_id = #{roadcross.id}
AND a.road_id = #{road.id}
AND a.address LIKE '%'||#{address}||'%' '%'+#{address}+'%' concat('%',#{address},'%')
AND a.nearest_poi LIKE '%'||#{nearestPoi}||'%' '%'+#{nearestPoi}+'%' concat('%',#{nearestPoi},'%')
AND a.send_by = #{sendBy.id}
AND DATE_FORMAT(a.send_date,'%Y-%m-%d') >= DATE_FORMAT(#{beginSendDate},'%Y-%m-%d')
AND DATE_FORMAT(a.send_date,'%Y-%m-%d') <= DATE_FORMAT(#{endSendDate},'%Y-%m-%d')
AND a.office = #{office.id}
AND a.leader_by = #{leaderBy.id}
AND DATE_FORMAT(a.job_begin_date,'%Y-%m-%d') >= DATE_FORMAT(#{beginJobBeginDate},'%Y-%m-%d')
AND DATE_FORMAT(a.job_begin_date,'%Y-%m-%d') <= DATE_FORMAT(#{endJobBeginDate},'%Y-%m-%d')
AND a.status = #{status}
AND a.malfunction_type = #{malfunctionType}
AND a.what_day = #{whatDay}
GROUP BY tmi.remarks, tmi.category, tmp.`name`, concat( tmi.remarks, ' ', tmi.category,'-',tmp.`name` ) , tmp.unit, tmi.price HAVING projectName is not null
ORDER BY tmi.category
ORDER BY tmi.remarks
ORDER BY tmp.unit
ORDER BY ${orderBy}
ORDER BY allMoney DESC
